CREATE TABLE Suppliers (
    supplier_id INT PRIMARY KEY,supplier_name VARCHAR(100),
    supplier_email VARCHAR(100),supplier_status VARCHAR(20) DEFAULT 'ACTIVE',
    supplier_rating DECIMAL(3,2) DEFAULT 5.00,contact_person VARCHAR(100),
    phone VARCHAR(20),address TEXT
);
CREATE TABLE Categories (
    category_id INT PRIMARY KEY,category_name VARCHAR(100),
    category_description TEXT,parent_category_id INT
);
CREATE TABLE Inventory (
    inventory_id INT PRIMARY KEY,product_name VARCHAR(100),
    category_id INT,supplier_id INT,
    current_stock INT DEFAULT 0,reorder_level INT DEFAULT 50,
    max_stock_level INT DEFAULT 1000,unit_cost DECIMAL(10,2),
    last_restock_date DATE,FOREIGN KEY (supplier_id) REFERENCES Suppliers(supplier_id),
    FOREIGN KEY (category_id) REFERENCES Categories(category_id)
);
CREATE TABLE Purchase_Orders (
    order_id INT PRIMARY KEY,supplier_id INT,order_date DATE,expected_delivery_date DATE,
    order_status VARCHAR(20) DEFAULT 'PENDING',total_amount DECIMAL(12,2),priority_level VARCHAR(10) DEFAULT 'MEDIUM',FOREIGN KEY (supplier_id) REFERENCES Suppliers(supplier_id)
);
CREATE TABLE Purchase_Order_Items (
    order_item_id INT PRIMARY KEY,order_id INT,
    inventory_id INT,quantity_ordered INT,
    unit_price DECIMAL(10,2),line_total DECIMAL(12,2),
    received_quantity INT DEFAULT 0,received_date DATE,
    quality_status VARCHAR(20) DEFAULT 'PENDING',FOREIGN KEY (order_id) REFERENCES Purchase_Orders(order_id),FOREIGN KEY (inventory_id) REFERENCES Inventory(inventory_id)
);
CREATE TABLE Shipments (
    shipment_id INT PRIMARY KEY,order_id INT,
    tracking_number VARCHAR(50),carrier_name VARCHAR(100),
    ship_date DATE,estimated_delivery_date DATE,
    actual_delivery_date DATE,shipment_status VARCHAR(20) DEFAULT 'PREPARING',
    delivery_address TEXT,shipping_cost DECIMAL(8,2),
    weight_kg DECIMAL(8,2),dimensions VARCHAR(50),
    FOREIGN KEY (order_id) REFERENCES Purchase_Orders(order_id)
);
CREATE TABLE Stores (
    store_id INT PRIMARY KEY,store_name VARCHAR(100),store_type VARCHAR(50),region VARCHAR(50),
    city VARCHAR(100),state VARCHAR(50),country VARCHAR(50),manager_name VARCHAR(100),
    phone VARCHAR(20),email VARCHAR(100),opening_date DATE,store_size_sqft INT,
    employee_count INT,revenue_tier VARCHAR(20),effective_start_date DATE DEFAULT '2024-01-01',effective_end_date DATE DEFAULT '9999-12-31',is_current BOOLEAN DEFAULT TRUE,record_version INT DEFAULT 1
);
