    employee_count INT,automation_level VARCHAR(20),climate_controlled BOOLEAN DEFAULT FALSE,hazmat_certified BOOLEAN DEFAULT FALSE,effective_start_date DATE DEFAULT '2024-01-01',effective_end_date DATE DEFAULT '9999-12-31',is_current BOOLEAN DEFAULT TRUE,record_version INT DEFAULT 1
);
CREATE TABLE Inventory (
    inventory_id INT PRIMARY KEY,warehouse_id INT,product_id INT,store_id INT,
    current_stock INT,reorder_point INT,max_stock_level INT,last_restock_date DATE,
    stock_value DECIMAL(12, 2),stock_status VARCHAR(20),days_of_supply INT,stock_out_risk_score DECIMAL(3, 2),
    last_movement_date DATE,movement_velocity VARCHAR(20),effective_start_date DATE DEFAULT '2024-01-01',effective_end_date DATE DEFAULT '9999-12-31',
    is_current BOOLEAN DEFAULT TRUE,record_version INT DEFAULT 1,created_timestamp TIMESTAMP DEFAULT CURRENT_TIMESTAMP,updated_timestamp TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    FOREIGN KEY (warehouse_id) REFERENCES Warehouses(warehouse_id),FOREIGN KEY (product_id) REFERENCES Products(product_id)
);
CREATE TABLE Shipments (
    shipment_id INT PRIMARY KEY,order_id INT,
    supplier_id INT,warehouse_id INT,
    shipment_date DATE,expected_delivery_date DATE,
    actual_delivery_date DATE,shipment_status VARCHAR(20),
    tracking_number VARCHAR(50),carrier_name VARCHAR(100),
    shipping_cost DECIMAL(10, 2),weight_kg DECIMAL(8, 2),
    volume_cubic_meters DECIMAL(8, 2),delivery_time_days INT,
    is_late BOOLEAN DEFAULT FALSE,delay_reason VARCHAR(200),
    quality_rating DECIMAL(2, 1),delivery_performance_score DECIMAL(3, 2),
    created_timestamp TIMESTAMP DEFAULT CURRENT_TIMESTAMP,updated_timestamp TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    FOREIGN KEY (order_id) REFERENCES Orders(order_id),FOREIGN KEY (supplier_id) REFERENCES Suppliers(supplier_id),
    FOREIGN KEY (warehouse_id) REFERENCES Warehouses(warehouse_id)
);
